jQuery(document).ready(function($){$("#mainBanner").owlCarousel({loop:true,margin:10,autoplay:true,autoplayTimeout:5000,autoplayHoverPause:false,responsiveClass:true,responsive:{0:{items:1,nav:true},600:{items:1,nav:false},1000:{items:1,nav:true,loop:true}}});$(".owl-prev").html('<i class="fa fa-chevron-left"></i>');$(".owl-next").html('<i class="fa fa-chevron-right"></i>');$("#our-clients").owlCarousel({rewindNav:false,singleItem:false,slideSpeed:300,paginationSpeed:400,navigation:false,responsive:{0:{items:1,nav:false},600:{items:2,nav:false},1000:{items:4,nav:false,loop:true}}});$('.count').each(function(){$(this).prop('Counter',0).animate({Counter:$(this).text()},{duration:1000,easing:'swing',step:function(now){$(this).text(Math.ceil(now));}});});$('.wpcf7-radio').children().children('input[type="radio"]').addClass('radio-custom');$('.wpcf7-radio').removeClass('radio-custom');$('.wpcf7-radio').children().children('span.wpcf7-list-item-label').addClass('radio-custom-label');$('html').addClass('no-js');});;(function(){var container,button,menu,links,i,len;container=document.getElementById('site-navigation');if(!container){return;}
button=container.getElementsByTagName('button')[0];if('undefined'===typeof button){return;}
menu=container.getElementsByTagName('ul')[0];if('undefined'===typeof menu){button.style.display='none';return;}
menu.setAttribute('aria-expanded','false');if(-1===menu.className.indexOf('nav-menu')){menu.className+=' nav-menu';}
button.onclick=function(){if(-1!==container.className.indexOf('toggled')){container.className=container.className.replace(' toggled','');button.setAttribute('aria-expanded','false');menu.setAttribute('aria-expanded','false');}else{container.className+=' toggled';button.setAttribute('aria-expanded','true');menu.setAttribute('aria-expanded','true');}};links=menu.getElementsByTagName('a');for(i=0,len=links.length;i<len;i++){links[i].addEventListener('focus',toggleFocus,true);links[i].addEventListener('blur',toggleFocus,true);}
function toggleFocus(){var self=this;while(-1===self.className.indexOf('nav-menu')){if('li'===self.tagName.toLowerCase()){if(-1!==self.className.indexOf('focus')){self.className=self.className.replace(' focus','');}else{self.className+=' focus';}}
self=self.parentElement;}}
(function(container){var touchStartFn,i,parentLink=container.querySelectorAll('.menu-item-has-children > a, .page_item_has_children > a');if('ontouchstart'in window){touchStartFn=function(e){var menuItem=this.parentNode,i;if(!menuItem.classList.contains('focus')){e.preventDefault();for(i=0;i<menuItem.parentNode.children.length;++i){if(menuItem===menuItem.parentNode.children[i]){continue;}
menuItem.parentNode.children[i].classList.remove('focus');}
menuItem.classList.add('focus');}else{menuItem.classList.remove('focus');}};for(i=0;i<parentLink.length;++i){parentLink[i].addEventListener('touchstart',touchStartFn,false);}}}(container));})();